import { useState } from 'react';
import { fetchSendMessage} from './services';
import ChatMessageList from './ChatMessageList';

const ChatOutgoing = ({username,onSend }) => {
  
  const [text, setText] = useState('');
  const [isGreyedout, setisGreyedout] = useState(true);
  const [isLoading, setisLoading] = useState(false);
  const [statusmsg, setstatusmsg] = useState('');
  const [messageList, setMessageList] = useState('');

  const onChange = (e) => {
      setstatusmsg('');
      setText(e.target.value);
      setisGreyedout(!e.target.value);
    };
    
  const sendmessage= ()=>{
    setisLoading(true);
    fetchSendMessage({username ,text})
    .then( chatList => {
      onSend({userList: chatList.userList, messageList:chatList.messageList });
      setText('');
      setstatusmsg('');
      setisLoading(false);
    })
    .catch( err => {
        setText('');
        setstatusmsg(err.error);
        setisLoading(false);
    });
  };
   const messagecontent = <ChatMessageList messageList = {messageList}/>;
    return (
        <div>
        {messagecontent}
        { statusmsg && <div class="status">{statusmsg}</div>}
        <label>
          Send Message:
          <input disabled={isLoading} onChange={onChange} value={text} />
        </label>
        <button onClick={sendmessage} disabled={isGreyedout || isLoading} >{ isLoading ? "..." : "Send"}</button>
      </div>
    );
  };
  
  export default ChatOutgoing;
