import './App.css';
import { useState, useEffect } from 'react';
import { checkSession } from './services';
import Login from './Login';
import ShowStuff from './ShowStuff';

function App() {
  const [userState,setUserState] = useState({ isLoggedIn: false, isPending: true });
  useEffect( () => {
    checkSession()
    .then( userinfo => {
      setUserState({
        isLoggedIn: true,
        isPending: false,
        username: userinfo.username,
        userList:userinfo.userList,
        messageList: userinfo.messageList,
      });
    })
    .catch( () => {
      // We treat any failure as not logged in
      setUserState({
        isLoggedIn: false,
        isPending: false,
      });
    });
  }, []); // only run on initial render

  const login = function({username,userinfo}) {
    setUserState({
      isLoggedIn: true,
      isPending: false,
      username,
      userinfo
      // userList,
      // messageList
    });
  };

  if(userState.isPending) {
    return (
      <div className="app">
        Loading...
      </div>
    );
  }

  let content;
  if(userState.isLoggedIn) {
      content = <ShowStuff stuff={userState.userState,userState.messageList}/>;
  } else {
    content = <Login onLogin={login}/>;
  }
  return (
    <div className="app">
     {content}
    </div>
  );
}

export default App;
