import { useState,useEffect } from 'react';
import { endSession, checkSession, fetchGetChat } from './services';
import ChatUserList from './ChatUserList';
import ChatMessageList from './ChatMessageList';
import ChatOutgoing from './ChatOutgoing';

const ShowStuff = ({ stuff }) => {
  // const [userState, setUserState] = useState({ isPending: true });
  const [chatState, setchatState] = useState('');
  const [userList, setUserList] = useState('');
  const [msgList, setmsgList] = useState('');
  useEffect( () => {
    fetchGetChat()
    .then( chat => {
      setchatState({
        // isPending: false,
        userList: chat.userList,
        messageList: chat.messageList,
      });
    })
    .catch( () => {
      // We treat any failure as not logged in
      setUserState({
        isPending: false,
      });
    });
  }, []); // only run on initial render

  const sendmessage = function({userList,messageList}) {
    setUserState({
      isPending: false,
      // username,
      userList,
      messageList
    });
    
  };
const usercontent   = <ChatUserList userList = {userState.userList}/>;
const messagecontent = <ChatMessageList messageList = {userState.messageList}/>;
const outgoingcontent = <ChatOutgoing username = {stuff.username} onSend = {sendmessage}/>;
  return (
    <div class = "display-panel">
      {usercontent}
      {messagecontent}
      {outgoingcontent}
    </div>
  );
};
export default ShowStuff;
