import { useEffect, useState } from 'react';
import { fetchEndSession, fetchCheckSession, fetchGetChat,fetchSendMessage } from './services';
// import ChatUserList from './ChatUserList';
// import ChatOutgoing from './ChatOutgoing';

const ShowStuff = ({ username }) => {

 
  const [userList, setUserList] = useState('');
  const [messageList, setMessageList] = useState('');
  const [error, setError] = useState('');

  const [text, setText] = useState('');
  const [isGreyedout, setisGreyedout] = useState(true);
  const [isLoading, setisLoading] = useState(false);
  const [statusmsg, setstatusmsg] = useState('');

const refreshTaskList=() => {
useEffect (()=>{
  fetchGetChat()
  .then((chatList)=>{
    setUserList(chatList.userList)
    setMessageList(chatList.messageList)
  })
  .catch((err) => {
    setError(err.error);
});
},[]);
}
setInterval(refreshTaskList,5000);
//  const sendmessage = function({userList,messageList}) {
//   setUserList(chatList.userList);
//   setMessageList(chatList.messageList)
//   };

const onChange = (e) => {
  setstatusmsg('');
  setText(e.target.value);
  setisGreyedout(!e.target.value);
};

const sendmessage= ()=>{
setisLoading(true);
fetchSendMessage({username ,text})
.then( chatList => {
  // onSend({userList: chatList.userList, messageList:chatList.messageList });
  setUserList(chatList.userList)
  setMessageList(chatList.messageList)
  setText('');
  setstatusmsg('');
  setisLoading(false);
  
})
.catch( err => {
    setText('');
    setstatusmsg(err.error);
    setisLoading(false);
});
};


  

// const usercontent   = <ChatUserList userList = {userList}/>;
// const outgoingcontent = <ChatOutgoing username = {username} messageList = {messageList} onSend = {sendmessage}/>;
  return (
    <div class = "display-panel">
      <div>
    <h3>User:</h3>
    <ul>
      {Object.values(userList).map( user => (
        <li>
            <div class="user">
            <span class="username">{user.username}</span>
            </div>
        </li> ) ) }
    </ul>
    </div>
    <div>
      <h3>Messages:</h3>
      <ul>
        {Object.values(messageList).map( message => (
          <li>
          <div class="messages">
            <span class = "messagetext">{message.sender}:{message.text}</span>
            <p>{message.timestamp}</p>
          </div>
        </li> ) ) }
      </ul>
      </div>
      <div>
        { statusmsg && <div class="status">{statusmsg}</div>}
        <label>
          Send Message:
          <input disabled={isLoading} onChange={onChange} value={text} />
        </label>
        <button onClick={sendmessage} disabled={isGreyedout || isLoading} >{ isLoading ? "..." : "Send"}</button>
      </div>
    </div>
  );
};
export default ShowStuff;
