import { useState } from 'react';
import { endSession, checkSession } from './services';
import ChatUserList from './ChatUserList';
import ChatMessageList from './ChatMessageList';
import ChatOutgoing from './ChatOutgoing';

const ShowStuff = ({ stuff }) => {
  const [userState, setUserState] = useState({ isPending: true });

  
  useEffect( () => {
    checkSession()
    .then( userinfo => {
      setUserState({
        isLoggedIn: true,
        isPending: false,
        username: userinfo.username,
        // userList:userinfo.userList,
        // messageList: userinfo.messageList,
      });
    })
    .catch( () => {
      // We treat any failure as not logged in
      setUserState({
        isLoggedIn: false,
        isPending: false,
      });
    });
  }, []); // only run on initial render

  const sendmessage = function({username, userList,messageList}) {
    setUserState({
      isPending: false,
      username,
      userList,
      messageList
    });
    // console.log("Hi");
    // <ShowStuff stuff={userState}/>;
    
  };
const usercontent   = <ChatUserList userList = {stuff.userList}/>;
const messagecontent = <ChatMessageList messageList = {stuff.messageList}/>;
const outgoingcontent = <ChatOutgoing username = {stuff.username} onSend = {sendmessage}/>;
  return (
    <div class = "display-panel">
      {usercontent}
      {messagecontent}
      {outgoingcontent}
    </div>
  );
};
export default ShowStuff;
