import { useState } from 'react';
import { sendMessage} from './services';
const ChatOutgoing = ({username }) => {
  const [text, setText] = useState('');
  const [isDisabled, setIsDisabled] = useState(true);
  const [isPending, setIsPending] = useState(false);
  const [status, setStatus] = useState('');

  const onChange = (e) => {
      setStatus('');
      setText(e.target.value);
      setIsDisabled(!e.target.value);
    };
    
  const sendmessage= ()=>{
    setIsPending(true);
    sendMessage({ username ,text})
    .then( userinfo => {
        setText('');
        setStatus('');
        setIsPending(false);
    })
    .catch( err => {
        setText('');
        setStatus(err.error);
        setIsPending(false);
    });
  };

    return (
        <div>
        { status && <div class="status">{status}</div>}
        <label>
          Send Message:
          <input disabled={isPending} onChange={onChange} value={text} />
        </label>
        <button onClick={sendmessage} disabled={isDisabled || isPending} >{ isPending ? "..." : "Send"}</button>
      </div>
    );
  };
  
  export default ChatOutgoing;
