import { useEffect, useState } from 'react';
import { fetchEndSession, fetchCheckSession, fetchGetChat } from './services';
import ChatUserList from './ChatUserList';
import ChatMessageList from './ChatMessageList';
import ChatOutgoing from './ChatOutgoing';

const ShowStuff = ({ stuff }) => {
  // const [userState, setUserState] = useState({ isPending: true });
  const [userList, setUserList] = useState('');
  const [messageList, setMessageList] = useState('');
  const [chatList, setChatList] = useState('');
  const [error, setError] = useState('');
  // const sendmessage = function({username, userList,messageList}) {
  //   setUserState({
  //     isPending: false,
  //     username,
  //     userList,
  //     messageList
  //   });   
  // };
useEffect (()=>{
  fetchGetChat()
  .then((chatList)=>{
    setUserList(chatList.userList)
    setMessageList(chatList.messageList)
  })
  .catch((err) => {
    setError(err.error);
});
},[]);
const usercontent   = <ChatUserList userList = {userList}/>;
const messagecontent = <ChatMessageList messageList = {messageList}/>;
// const outgoingcontent = <ChatOutgoing username = {stuff.username} onSend = {sendmessage}/>;
  return (
    <div class = "display-panel">
      {usercontent}
    </div>
  );
};
export default ShowStuff;
